import { useState, useEffect } from "react"
import { useLocation, useNavigate, useParams } from "react-router"
import FormDialog from "../components/FormDialog";
import Grid2 from "@mui/material/Unstable_Grid2";
import Item from "../components/Item"
import idosos from '../idososDetalhes.json'
import capa from '../assets/capa.jpg'

export default function Idosos() {
  let { state } = useLocation();

  const [idosoList, setIdosoList] = useState([])
  const [buttonCount, setButtonCount] = useState(false)
  const navigate = useNavigate();

  useEffect(() => {
    setTimeout(() => {
     
    }, 1000);
  }, [buttonCount]);

  useEffect(() => {
    setIdosoList(idosos.arrayidoso)
  }, [])

  return (
    <div className="content-idoso">
      <header>
        <div className="user">
          <span>Usu√°rio</span>
        </div>
      </header>

      <section className="capa">
        <img src={capa} alt="Capa"/>
      </section>

      <div>
                <button className="back" onClick={() => navigate('/home')}>Voltar</button>
                <div>
                    <img className="detailed-image" src={state.i.image} alt="" />
                </div>
                <div>
                    <h2>{state.i.title} - {id}</h2>
                    <p>{state.i.description}</p>

                    <Grid2
                        container
                        direction="row"
                        justifyContent="center"
                        alignItems="center">
                       
                       <FormDialog />

                    </Grid2> 

                </div>
            </div>
      <section className="main-idosos">
        {idosoList.map((i, index) => (
          <Item key={index} idoso={i} buttonCount={buttonCount} setButtonCount={setButtonCount}/>
        ))}
      </section>
      <footer></footer>
    </div>
  )
}
